import React, { useState } from "react";
import "../styles/ListItem.css";
import { Buton } from "./Buton";
import { NewInput } from "./Input";

import { ReactComponent as DeleteIcon } from "../images/delete.svg";
import { ReactComponent as EditIcon } from "../images/edit.svg";

export const ListItem = ({
  handleDisabled,
  className,
  status,
  idx,
  handleDelete,

  //
  item,
  onItemChange,
}) => {
  const [editedTodo, setEditedTodo] = useState("");
  const [isInEdit, setIsInEdit] = useState(false);

  const toggleEdit = () => {
    setIsInEdit((s) => !s);
    setEditedTodo(item.value);
  };

  return (
    <div className="containerLine">
      {isInEdit ? (
        <NewInput
          value={editedTodo}
          onChange={setEditedTodo}
          onAdd={() => {
            console.log("fa ceva cu mine...");
            onItemChange(idx, editedTodo);
            setIsInEdit(false);
          }}
        />
      ) : (
        <>
          <div onClick={handleDisabled} className={className}>
            {item.value}
          </div>
          {status && (
            <>
              <Buton onClick={toggleEdit}>
                <EditIcon className="actionIcon edit" />
              </Buton>

              <Buton onClick={handleDelete}>
                <DeleteIcon className="actionIcon delete" />
              </Buton>
            </>
          )}
        </>
      )}
    </div>
  );
};
