import React from "react";
import { ReactComponent as DeleteIcon } from "../images/delete.svg";
import { ReactComponent as EditIcon } from "../images/edit.svg";

import "../styles/Buton.css";

export const Buton = ({ type, handleEdit, handleDelete, toggleEdit }) => {
  return (
    <>
      {type === "edit" ? (
        <div
          onClick={() => {
            toggleEdit();
            handleEdit();
          }}
          className="actionButton"
        >
          <EditIcon
            onClick={() => {
              toggleEdit();
              handleEdit();
            }}
            className="actionIcon edit"
          />
        </div>
      ) : (
        <div className="actionButton delete">
          <DeleteIcon onClick={handleDelete} className="actionIcon" />
        </div>
      )}
    </>
  );
};
