import React, { useState } from "react";
import "../styles/Todo.css";

import { NewInput } from "../components/Input";
import { ListItem } from "../components/ListItem";

export const Todo = () => {
  const [todo, setTodo] = useState("");
  const [listItems, setListItems] = useState([]);

  const handleAddItem = () => {
    setListItems([{ value: todo, enabled: true }, ...listItems]);
    setTodo("");
  };

  const handleDisabled = (idx) => () => {
    setListItems([
      ...listItems.slice(0, idx),
      { value: listItems[idx].value, enabled: !listItems[idx].enabled },
      ...listItems.slice(idx + 1, listItems.length),
    ]);
  };

  const handleDelete = (idx) => () => {
    setListItems([
      ...listItems.slice(0, idx),
      ...listItems.slice(idx + 1, listItems.length),
    ]);
  };

  const handleEdit = (idx, newText) => {
    setListItems([
      ...listItems.slice(0, idx),
      { value: newText, enabled: listItems[idx].enabled },
      ...listItems.slice(idx + 1, listItems.length),
    ]);
  };

  return (
    <div className="todo">
      <div className="contentWrapper">
        <h1 className="title">ToDo List</h1>
        <NewInput value={todo} onChange={setTodo} onAdd={handleAddItem} />

        <div className="listWrapper">
          {listItems.map((item, idx) => {
            return (
              <ListItem
                key={idx}
                idx={idx}
                className={
                  listItems[idx].enabled
                    ? "listItemEnabled"
                    : "listItemDisabled"
                }
                item={item}
                onItemChange={handleEdit}
                //
                handleDisabled={handleDisabled(idx)}
                status={listItems[idx].enabled}
                handleDelete={handleDelete(idx)}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
};
