import React, { useState } from "react";
import { ReactComponent as AddIcon } from "../images/add.svg";
import "../styles/Input.css";

export const Input = ({
  handleAddItem,
  handleEdit,
  type,
  idx,
  oldText,
  toggleEdit,
}) => {
  const [searchItem, setSearchItem] = useState("");

  return (
    <div className="containerInput">
      <input
        value={type === "add" ? searchItem : oldText}
        className="input"
        onChange={(e) => setSearchItem(e.target.value)}
        placeholder="Write text here"
      ></input>
      <AddIcon
        className="img"
        onClick={
          type === "add"
            ? searchItem
              ? () => {
                  setSearchItem("");
                  handleAddItem(searchItem);
                }
              : null
            : () => {
                handleEdit(idx, searchItem);
                toggleEdit();
                console.log("din confirm edit");
              }
        }
      />
    </div>
  );
};
