import React, { useState } from "react";
import "../styles/Todo.css";

import { Input } from "../components/Input";
import { ListItem } from "../components/ListItem";

export const Todo = () => {
  const [listItems, setListItems] = useState([]);

  const handleAddItem = (item) => {
    setListItems([{ item, enabled: true }, ...listItems]);
  };

  const handleDisabled = (idx) => () => {
    setListItems([
      ...listItems.slice(0, idx),
      { item: listItems[idx].item, enabled: !listItems[idx].enabled },
      ...listItems.slice(idx + 1, listItems.length),
    ]);
  };

  const handleDelete = (idx) => () => {
    setListItems([
      ...listItems.slice(0, idx),
      ...listItems.slice(idx + 1, listItems.length),
    ]);
  };

  const handleEdit = (idx, newText) => () => {
    // setListItems([
    //   ...listItems.slice(0, idx),
    //   { item: newText, enabled: listItems[idx].enabled },
    //   ...listItems.slice(idx + 1, listItems.length),
    // ]);
  };

  return (
    <div className="todo">
      <div className="contentWrapper">
        <h1 className="title">ToDo List</h1>
        <Input handleAddItem={handleAddItem} />

        <div className="listWrapper">
          {listItems.map((itemObject, idx) => {
            return (
              <ListItem
                key={idx}
                id={idx}
                className={
                  listItems[idx].enabled
                    ? "listItemEnabled"
                    : "listItemDisabled"
                }
                handleDisabled={handleDisabled(idx)}
                status={listItems[idx].enabled}
                handleDelete={handleDelete(idx)}
                handleEdit={handleEdit(idx)}
              >
                {itemObject.item}
              </ListItem>
            );
          })}
        </div>
      </div>
    </div>
  );
};
