import "../styles/ListItem.css";
import { Buton } from "./Buton";

export const ListItem = ({
  children,
  handleDisabled,
  className,
  status,
  idx,
  handleDelete,
  handleEdit,
}) => {
  cosnt[(isInEdit, setIsInEdit)] = useState(false);
  return (
    <div className="containerLine">
      <div onClick={handleDisabled} className={className}>
        {children}
      </div>
      {status ? (
        <>
          <Buton handleEdit={handleEdit} type={"edit"} />
          <Buton handleDelete={handleDelete} type={"delete"} />
        </>
      ) : (
        <></>
      )}
    </div>
  );
};
